// client/src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Dashboard = () => {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState('');
  const [status, setStatus] = useState('Pending');
  const [editId, setEditId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editStatus, setEditStatus] = useState('Pending');
  const history = useHistory();

  useEffect(() => {
    const fetchTodos = async () => {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/todos', {
        headers: { Authorization: token },
      });
      setTodos(response.data);
    };
    fetchTodos();
  }, []);

  const handleAddTodo = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const response = await axios.post('http://localhost:5000/api/todos', { title, status }, {
      headers: { Authorization: token },
    });
    setTodos([...todos, response.data]);
    setTitle('');
    setStatus('Pending');
  };

  const handleEditTodo = (todo) => {
    setEditId(todo.id);
    setEditTitle(todo.title);
    setEditStatus(todo.status);
  };

  const handleUpdateTodo = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const response = await axios.put(`http://localhost:5000/api/todos/${editId}`, { title: editTitle, status: editStatus }, {
      headers: { Authorization: token },
    });
    const updatedTodos = todos.map(todo => (todo.id === editId ? response.data : todo));
    setTodos(updatedTodos);
    setEditId(null);
    setEditTitle('');
    setEditStatus('Pending');
  };

  const handleDeleteTodo = async (id) => {
    const token = localStorage.getItem('token');
    await axios.delete(`http://localhost:5000/api/todos/${id}`, {
      headers: { Authorization: token },
    });
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear the token from local storage
    history.push('/login'); // Redirect to login page
  };

  return (
    <div>
      <h2 className="text-center mt-4">Todo List</h2>
      <button className="btn btn-danger" onClick={handleLogout}>Logout</button>

      {/* Add Todo Form */}
      <form onSubmit={handleAddTodo}>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Add new todo"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <select className="form-select" value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
          <button className="btn btn-success" type="submit">Add</button>
        </div>
      </form>

      {/* Edit Todo Form */}
      {editId && (
        <form onSubmit={handleUpdateTodo}>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              required
            />
            <select className="form-select" value={editStatus} onChange={(e) => setEditStatus(e.target.value)}>
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
            <button className="btn btn-warning" type="submit">Update</button>
            <button className="btn btn-secondary" onClick={() => setEditId(null)}>Cancel</button>
          </div>
        </form>
      )}

      {/* Todo List */}
      <ul className="list-group">
        {todos.map(todo => (
          <li key={todo.id} className="list-group-item d-flex justify-content-between align-items-center">
            <span>{todo.title} ({todo.status})</span>
            <div>
              <button className="btn btn-info" onClick={() => handleEditTodo(todo)}>Edit</button>
              <button className="btn btn-danger" onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
